        CLOSE tareas_cursor;
				
				--update SGDP_PROCESOS con registro_proceso.ID_PROCESO A registro_proceso."B_VIGENTE" = FALSE
				--UPDATE "SGDP_PROCESOS" SET "B_VIGENTE" = FALSE WHERE "ID_PROCESO" = registro_proceso."ID_PROCESO"

